import React,{ useState,useReducer, useContext } from "react";

function reducer(state,action){
    switch(action.type){
        case "addTodo":
            return {...state,todos:[...state,{ text:action.payload}] }
            
        case "delete":
             return {...state ,todos:state.find((t) => t.id != action.payload) }
        
        case "toggle":
            return {...state,completed: prev => !prev}

    }

}
const initialState = {
    todos:[],
    completed : false

}

function Todo(){
    const [state,dispatch] = useReducer(reducer,initialState)
    const [text,setText] = useState('')

    const addtodo = () =>{
        dispatch({type:"addTodo" ,payload:text})
        setText('')
    }
    return(
        <>
        <h2>Todo List with usereducer</h2>
        <input value={text} onChange={(e) => setText(e.target.value)} type="text" placeholder=" enter something"/>
        <button onClick={addtodo}>ADD</button>
        
        {state.todos.map((item) =>(
            <li key={item.id}>
                {item.text}
                <button onClick={() => dispatch({type:'toggle',payload:item.completed})}>Done</button>
                <button onClick={() => dispatch({type:'delete',payload:item.id})}>Delete</button>
            </li>
        ) )}


        </>
    )
}

export default Todo
