import React,{useEffect,useState} from "react";


function Time(){
    const [time,setTime] = useState(new Date().toLocaleTimeString)

    useEffect(() =>{
     const interval = setInterval(() =>{
        setTime(new Date().toLocaleTimeString())
     },1000)

     return () => clearInterval(interval)
    },[])

    return(
        <>
     <h1>Current Time </h1>
      <h2>{time}</h2>
        </>
    )

}

export default Time
