import React,{useState} from "react";



function Todo(){
     const [todo,setTodo] = useState([])
     const [text,setText] = useState('')
     const [editindex,setEditIndex] = useState(0)
     const [editText,setEditText] = useState('')

     const addtodo = () =>{
        setTodo([...todo,text])
        setText('')
     }

    const delete1 = (index) =>{
         const todos = todo.filter((_,i) => i!== index)
         setTodo(todos) 
    }
    const edit = (index) =>{
       setEditIndex(index)
       setEditText(todo[index])
    }

    const save = () =>{
        const updated = [...todo]
        updated[editindex] = editText
        setTodo(updated)
        setEditIndex(0)
        setEditText('')
    }


    return(
        <>
        <h1>{text}</h1>
        <input onChange={(e) => setText(e.target.value)} placeholder=" write next todo item" />
        <button onClick={addtodo} >ADD</button>

        
            <ul>
                {todo.map((item,index) =>(
                    <li key={index}>{item}
                    { editindex === index ? (<>                    
                    <button onClick={() => delete1(index)}>Delete</button>
                    <button onClick={() => edit(index)}>Edit</button>
                </>
                ):(
                    <>
                    <button onClick={save} >save</button>
                    </>
                )}</li>
                ))}
            </ul>
         

        
        </>
    )


}

export default Todo
