    const matching =() =>{
        if(text1 === text2){
            setResult('matched')
        }
    }

    return(
        <>
        { result && (<p>...{result}</p>)}
        <input onChange={(e) => setText1(e.target.value)}/>
        <input onChange={(e) => setText2(e.target.value)}/>
        
        
        </>
    )


}

export default Match
