import React,{useReducer} from "react";

function reducer(state,action){
    switch (action.type){
        case "setnum1":
            return {...state,num1:action.paylod}
        case "setnum2":
            return {...state,num2:action.paylod}
        default:
            return state
    }
}

function Add(){
    const [state,dispatch] = useReducer(reducer,{num1:0,num2:0})
    const result = Number(state.num1) + Number(state.num2)
    return(<>
        <h1>Result:{result}</h1>
        <input onChange={(e) => dispatch({type:'setnum1',num1:e.target.value})} placeholder="enter your 1st number"/>
        <input onChange={(e) => dispatch({type:'setnum2',num2:e.target.value})} placeholder="enter your 2nd number"/>
    </>)

}

export default Add
