import React,{ useMemo,useState} from "react";


function Addtwonum(){
    const [num1,setNum1] = useState(0)
 
    const result = useMemo(() => {
        return num1 * num1
    },[num1])

    return(
        <>
        <h2>result : {result}</h2>
        <input onChange={(e) => setNum1(Number(e.target.value))} type="number" />


        </>
    )

}

export default Addtwonum
