import React,{Component} from "react";

class Test extends Component{

    state = {
        text : ''
    }

    handleChange = (e) =>{
        this.setState({text : e.target.value })
    }

    render(){
        return(
            <>
            <h1>{this.state.text}</h1>
            <input value={text} onChange={this.handleChange} placeholder="enter anything "/>
            </>
        )
    }


}
export default Test
