import React,{useState} from "react";



function Todo(){
     const [todo,setTodo] = useState([])
     const [text,setText] = useState('')

     const [editindex,setEditIndex] = useState

     const addtodo = () =>{
        setTodo([...todo,text])
        setText('')
     }

    const delete1 = ({index}) =>{
         const todos = todo.filter((i) => i!== index)
         setTodo(todos) 
    }
    const edit = ({index}) =>{
        const updatedtodo = [...todo]
       
       setTodo(updatedtodo)
    }
    return(
        <>
        <h1>{text}</h1>
        <input onChange={(e) => setText(e.target.value)} placeholder=" write next todo item" />
        <button onClick={addtodo} >ADD</button>

        
            <ul>
                {todo.map((item,index) =>{
                    <li key={index}>{item }
                    <button onClick={delete1(index)}>Delete</button>
                    <button onClick={edit(index)}>Edit</button>
                    </li>
                })}
            </ul>
         

        
        </>
    )


}

export default Todo
